import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Assignment, AssignmentSubmission } from '../../types';

@Component({
  selector: 'app-assignment-grading-modal',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './assignment-grading-modal.component.html',
})
export class AssignmentGradingModalComponent implements OnInit {
  @Input() assignment!: Assignment;
  @Input() submission!: AssignmentSubmission;
  @Input() wardName!: string;
  @Output() close = new EventEmitter<void>();
  @Output() saveGrade = new EventEmitter<{wardId: string, grade: number, feedback: string}>();

  grade: string = '';
  feedback: string = '';

  ngOnInit(): void {
    this.grade = this.submission.grade?.toString() || '';
    this.feedback = this.submission.feedback || '';
  }

  handleSave(): void {
    const numericGrade = parseInt(this.grade, 10);
    if (!isNaN(numericGrade) && numericGrade >= 0 && numericGrade <= 100) {
      this.saveGrade.emit({
        wardId: this.submission.wardId,
        grade: numericGrade,
        feedback: this.feedback
      });
    } else {
      alert("Please enter a valid grade between 0 and 100.");
    }
  }
}
<div class="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50 p-4">
    <div class="bg-white dark:bg-slate-800 rounded-lg shadow-xl p-6 w-full max-w-3xl max-h-[90vh] flex flex-col">
      <div class="flex justify-between items-center mb-4 pb-4 border-b border-slate-200 dark:border-slate-700">
        <div>
          <h2 class="text-2xl font-bold text-slate-800 dark:text-white">Grade Assignment</h2>
          <p class="text-sm text-slate-500 dark:text-slate-400">{{ assignment.title }} - {{ wardName }}</p>
        </div>
        <button (click)="close.emit()" class="text-slate-400 hover:text-slate-600 dark:hover:text-slate-200">
          <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
      </div>

      <div class="grid md:grid-cols-2 gap-6 overflow-y-auto pr-2 flex-grow">
          <div class="space-y-4">
              <div>
                  <h3 class="font-semibold text-slate-700 dark:text-slate-200 mb-2">Student's Submission</h3>
                  <div class="bg-slate-50 dark:bg-slate-700/50 p-4 rounded-lg text-sm text-slate-600 dark:text-slate-300 whitespace-pre-wrap h-96 overflow-y-auto">
                      {{ submission.content }}
                  </div>
              </div>
          </div>
          <div class="space-y-4">
              <div>
                  <label for="grade" class="block text-sm font-medium text-slate-700 dark:text-slate-300">Grade (%)</label>
                  <input
                      type="number"
                      id="grade"
                      [(ngModel)]="grade"
                      class="mt-1 block w-full px-3 py-2 bg-white dark:bg-slate-700 border border-slate-300 dark:border-slate-600 rounded-md shadow-sm"
                      placeholder="0-100"
                  />
              </div>
              <div>
                  <label for="feedback" class="block text-sm font-medium text-slate-700 dark:text-slate-300">Feedback</label>
                  <textarea
                      id="feedback"
                      rows="10"
                      [(ngModel)]="feedback"
                      class="mt-1 block w-full px-3 py-2 bg-white dark:bg-slate-700 border border-slate-300 dark:border-slate-600 rounded-md shadow-sm"
                      placeholder="Provide constructive feedback..."
                  ></textarea>
              </div>
          </div>
      </div>

      <div class="mt-6 flex justify-end space-x-3 pt-4 border-t border-slate-200 dark:border-slate-700">
        <button (click)="close.emit()" class="px-4 py-2 border border-slate-300 text-sm font-medium rounded-md">
          Cancel
        </button>
        <button (click)="handleSave()" class="px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700">
          Save Grade & Feedback
        </button>
      </div>
    </div>
</div>
